import React from 'react';

import './App.css';
const apiKey = '53df5b52f003d1bd1df16a7f6f299393';


class App extends React.Component{//初始化值
       constructor(){
       super();
      this.state={
       zipcode:'27403',

       weather:null,


      }
      this.handleChange  = this.handleChange.bind(this); 
      this.onChange = this.onChange.bind(this);
}

async componentDidMount(){   //获取天气
  const weather=await this.fetchWeather();
      this.setState(
        {
          weather,

        }
      );
      console.log(weather)
      this.onChange = this.onChange.bind(this);

}

fetchWeather=async () =>{// 读取天气

     return await fetch(`http://api.openweathermap.org/data/2.5/forecast?zip=${this.state.zipcode}&appid=53df5b52f003d1bd1df16a7f6f299393

        `).then(data=>{ return data.json();})


        }//api.openweathermap.org/data/2.5/weather?zip=${zipcode}&appid=${apiKey}`,换成这个无法获取



  render(){


    const list = () => {
      let {weather}=this.state;
      const res = [];
      for(let i = 0; i < weather.list.length; i++) {
        res.push(<li key={i}> <span className="colorFont">第{i+1}天  </span>  {weather.list[i].main.temp}</li>)
      }
      return res
    }


    if(!this.state.weather){
      return null;
    }
    if(!this.state.zipcode){
      return null;
    }


    let {weather}=this.state;

        return(

            <div className="App">
              <h2> {weather.city.country}  {weather.city.name}  {weather.cnt} 天未来天气预测</h2>
            <ul className="deteleUnderLine">

           {list()  }   {/*调用函数释*/}
            </ul>
            <input type="text" value={this.state.value} onChange={this.handleChange} placeholder="Zip Code"/>

          </div>


        )

  }

handleChange(event) {
  this.setState(
    {zipcode: event.target.value}
    );
  }

}
export default App;
