import React from 'react';

import './App.css';


class WeatherForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        Zipcode: ''
      }; 

      this.onChange = this.onChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);


    }

    // prevent submit from refreshing page 
    handleSubmit(event){
      console.log("clicked");
      event.preventDefault(); 
    }

    // grab data and update state with value 
    onChange(event){
      console.log("this is event", event); 
      this.setState = {
        zip: event.target.value
      }


    }
    // how do i get this to show up? 
    // how to share these properties with the app
    // is this even the right way??
    render() {
      return (
        <div className="Form">
          <form onSubmit={this.handleSubmit}>
          <lable>Enter Zipcode
          <input type="text" value={this.state.zip} onChange={this.onChange} />
          </lable>
          <input type="submit" value="Submit" /> 
          </form>
        </div>
      );
    }
  }
  export default WeatherForm;
